import React from "react";

const HospitalDirections = () => {
  // Example hospitals in Erode (you can replace with real ones)
  const hospitals = [
    {
      name: "KMCH Hospital Erode",
      url: "https://www.google.com/maps/dir/?api=1&destination=KMCH+Hospital+Erode"
    },
    {
      name: "Lotus Hospital Erode",
      url: "https://www.google.com/maps/dir/?api=1&destination=Lotus+Hospital+Erode"
    },
    {
      name: "Meridian Hospital Erode",
      url: "https://www.google.com/maps/dir/?api=1&destination=Meridian+Hospital+Erode"
    },
    {
      name: "Vel Hospital Erode",
      url: "https://www.google.com/maps/dir/?api=1&destination=Vel+Hospital+Erode"
    },
    {
      name: "Kongu Hospital Erode",
      url: "https://www.google.com/maps/dir/?api=1&destination=Kongu+Hospital+Erode"
    }
  ];

  return (
    <div>
      <h2>Erode Hospitals Directions</h2>
      <p>Click below to see directions from your current location:</p>
      <ul>
        {hospitals.map((hospital, index) => (
          <li key={index}>
            <a
              href={hospital.url}
              target="_blank"
              rel="noopener noreferrer"
            >
              {hospital.name}
            </a>
          </li>
        ))}
      </ul>

      {/* Show map for first hospital as example */}
      <h3>Map Preview - KMCH Hospital</h3>
      <iframe
        title="KMCH Hospital Erode"
        src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3914.37214552495!2d77.7271!3d11.341!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3ba96f6510a8a3db%3A0x12345!2sKMCH%20Hospital%20Erode!5e0!3m2!1sen!2sin!4v1234567890"
        width="600"
        height="450"
        style={{ border: 0 }}
        allowFullScreen=""
        loading="lazy"
      ></iframe>
    </div>
  );
};

export default HospitalDirections;
