import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const Home = () => {
  const { user } = useAuth();
  const [currentSlide, setCurrentSlide] = useState(0);

  const slides = [
    {
      id: 1,
      title: "Save Lives Through Blood Donation",
      description: "Every drop counts. Join our mission to save lives.",
      image: process.env.PUBLIC_URL + "/images/poster1.svg"
    },
    {
      id: 2,
      title: "Emergency Blood Supply",
      description: "24/7 availability for emergency situations.",
      image: process.env.PUBLIC_URL + "/images/poster2.svg"
    },
    {
      id: 3,
      title: "Community Blood Drives",
      description: "Organize and participate in community blood donation events.",
      image: process.env.PUBLIC_URL + "/images/poster3.svg"
    }
  ];

  useEffect(() => {
    const t = setInterval(() => {
      setCurrentSlide((p) => (p + 1) % slides.length);
    }, 5000);
    return () => clearInterval(t);
  }, [slides.length]);

  return (
    <div className="min-h-screen">
      {/* HERO */}
      <section className="relative h-96 md:h-[500px] overflow-hidden isolate">
        {slides.map((slide, index) => (
          <div
            key={slide.id}
            className={`absolute inset-0 transition-opacity duration-1000 ${
              index === currentSlide ? 'opacity-100' : 'opacity-0 pointer-events-none'
            }`}
            aria-hidden={index !== currentSlide}
          >
            {/* Background image */}
            <img
              src={slide.image}
              alt=""
              className="w-full h-full object-cover"
            />

            {/* Global dark gradient to suppress poster text */}
            <div className="absolute inset-0 z-10 bg-gradient-to-b from-black/70 via-black/60 to-black/40" />

            {/* Centered content with its own soft backdrop */}
            <div className="absolute inset-0 z-20 flex items-center justify-center px-4">
              <div className="text-center text-white">
                <div className="inline-block rounded-2xl bg-black/35 backdrop-blur-sm px-6 py-5 md:px-10 md:py-6 shadow-lg">
                  <h1 className="text-4xl md:text-6xl font-extrabold mb-3">
                    {slide.title}
                  </h1>
                  <p className="text-lg md:text-2xl mb-6 opacity-95">
                    {slide.description}
                  </p>
                  {!user && (
                    <div className="flex items-center justify-center gap-4">
                      <Link to="/register" className="btn-primary text-lg px-8 py-3">
                        Get Started
                      </Link>
                      <Link
                        to="/login"
                        className="btn-outline text-lg px-8 py-3 border-white text-white hover:bg-white hover:text-primary-600"
                      >
                        Sign In
                      </Link>
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>
        ))}

        {/* Dots */}
        <div className="absolute bottom-8 left-1/2 -translate-x-1/2 z-30 flex space-x-2">
          {slides.map((_, i) => (
            <button
              key={i}
              onClick={() => setCurrentSlide(i)}
              className={`w-3 h-3 rounded-full transition-colors ${
                i === currentSlide ? 'bg-white' : 'bg-white/50'
              }`}
            />
          ))}
        </div>
      </section>

      {/* SERVICES */}
      <section className="py-16 bg-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-12">
            <h2 className="text-3xl font-bold text-secondary-800 mb-4">Our Services</h2>
            <p className="text-xl text-secondary-600">
              Everything you need for blood donation and management
            </p>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            <div className="card text-center hover:shadow-xl transition-shadow duration-200">
              <div className="text-4xl mb-4">🔍</div>
              <h3 className="text-xl font-semibold text-secondary-800 mb-3">
                Blood Availability Search
              </h3>
              <p className="text-secondary-600 mb-4">
                Search for available blood by blood group and request donors if needed
              </p>
              <Link to="/availability" className="btn-primary">Search Blood</Link>
            </div>

            <div className="card text-center hover:shadow-xl transition-shadow duration-200">
              <div className="text-4xl mb-4">🏥</div>
              <h3 className="text-xl font-semibold text-secondary-800 mb-3">
                Blood Center Directory
              </h3>
              <p className="text-secondary-600 mb-4">
                Find blood banks and donation centers near you with detailed information
              </p>
              <Link to="/directory" className="btn-primary">Find Centers</Link>
            </div>

            <div className="card text-center hover:shadow-xl transition-shadow duration-200">
              <div className="text-4xl mb-4">🎪</div>
              <h3 className="text-xl font-semibold text-secondary-800 mb-3">
                Blood Donation Camps
              </h3>
              <p className="text-secondary-600 mb-4">
                Discover upcoming blood donation events and camps in your area
              </p>
              <Link to="/camps" className="btn-primary">View Camps</Link>
            </div>

            <div className="card text-center hover:shadow-xl transition-shadow duration-200">
              <div className="text-4xl mb-4">🔐</div>
              <h3 className="text-xl font-semibold text-secondary-800 mb-3">
                Donor Login
              </h3>
              <p className="text-secondary-600 mb-4">
                Access your donor account to manage donations and requests
              </p>
              {!user ? (
                <Link to="/login" className="btn-primary">Login</Link>
              ) : (
                <div className="text-primary-600 font-medium">Welcome back, {user.name}!</div>
              )}
            </div>

            <div className="card text-center hover:shadow-xl transition-shadow duration-200">
              <div className="text-4xl mb-4">📝</div>
              <h3 className="text-xl font-semibold text-secondary-800 mb-3">
                Register Voluntary Blood Camp
              </h3>
              <p className="text-secondary-600 mb-4">
                Organize your own blood donation event and help save lives
              </p>
              <Link to="/register-camp" className="btn-primary">Register Camp</Link>
            </div>

            <div className="card text-center hover:shadow-xl transition-shadow duration-200">
              <div className="text-4xl mb-4">📚</div>
              <h3 className="text-xl font-semibold text-secondary-800 mb-3">
                Learn About Donation
              </h3>
              <p className="text-secondary-600 mb-4">
                Get educated about blood donation process, benefits, and myths
              </p>
              <Link to="/learn" className="btn-primary">Learn More</Link>
            </div>
          </div>
        </div>
      </section>

      {/* CTA */}
