import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api';

// Create axios instance
const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Request interceptor to add auth token
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Response interceptor for error handling
api.interceptors.response.use(
  (response) => response,
  (error) => {
    console.error('API Error:', error.response?.data || error.message);
    
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

// Auth API calls
export const authAPI = {
  register: (userData) => api.post('/auth/register', userData),
  login: (credentials) => api.post('/auth/login', credentials),
  getProfile: () => api.get('/auth/me'),
};

// Blood API calls
export const bloodAPI = {
  checkAvailability: (bloodGroup) => api.get(`/blood/availability/${bloodGroup}`),
  createRequest: (requestData) => api.post('/blood/request', requestData),
  getRequests: () => api.get('/blood/requests'),
  getMyRequests: () => api.get('/blood/requests/my'),
  updateRequestStatus: (id, status) => api.put(`/blood/requests/${id}/status`, { status }),
};

// Voluntary Camp API calls
export const voluntaryCampAPI = {
  register: (campData) => api.post('/voluntary-camp/register', campData),
  getAll: () => api.get('/voluntary-camp/all'),
  getPending: () => api.get('/voluntary-camp/pending'),
  updateStatus: (id, status) => api.put(`/voluntary-camp/${id}/status`, { status }),
};

export default api;
