import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const LearnAboutDonation = () => {
  const [activeTab, setActiveTab] = useState('basics');

  const tabs = [
    { id: 'basics', label: 'Basics', icon: 'üìö' },
    { id: 'process', label: 'Process', icon: 'ü©∏' },
    { id: 'eligibility', label: 'Eligibility', icon: '‚úÖ' },
    { id: 'benefits', label: 'Benefits', icon: '‚ù§Ô∏è' },
    { id: 'myths', label: 'Myths', icon: '‚ùì' },
    { id: 'faq', label: 'FAQ', icon: 'üí¨' }
  ];

  const content = {
    basics: {
      title: "Blood Donation Basics",
      content: [
        {
          subtitle: "What is Blood Donation?",
          text: "Blood donation is the process of voluntarily giving blood to help others in need. Your donation can save up to three lives and is essential for medical procedures, surgeries, and emergency situations.",
          icon: "ü©∏"
        },
        {
          subtitle: "Types of Blood Donations",
          text: "There are several types of blood donations: whole blood donation (most common), platelet donation, plasma donation, and double red cell donation. Each serves different medical needs.",
          icon: "üè•"
        },
        {
          subtitle: "Blood Components",
          text: "Blood consists of red blood cells (carry oxygen), white blood cells (fight infection), platelets (help clotting), and plasma (liquid portion). Each component can be used to treat different conditions.",
          icon: "üî¨"
        }
      ]
    },
    process: {
      title: "The Donation Process",
      content: [
        {
          subtitle: "Before Donation",
          text: "Eat a healthy meal, drink plenty of water, get adequate sleep, and bring a valid ID. Avoid fatty foods and alcohol 24 hours before donation.",
          icon: "üçΩÔ∏è"
        },
        {
          subtitle: "During Donation",
          text: "The process takes about 8-10 minutes. A sterile needle is used to collect approximately 1 pint of blood. You'll be monitored throughout the process.",
          icon: "‚è±Ô∏è"
        },
        {
          subtitle: "After Donation",
          text: "Rest for 10-15 minutes, drink extra fluids, avoid heavy lifting for 24 hours, and eat iron-rich foods to help replenish your blood supply.",
          icon: "üîÑ"
        }
      ]
    },
    eligibility: {
      title: "Donor Eligibility",
      content: [
        {
          subtitle: "Basic Requirements",
          text: "You must be at least 18 years old (or 16-17 with parental consent in some states), weigh at least 110 pounds, and be in good general health.",
          icon: "üìã"
        },
        {
          subtitle: "Health Conditions",
          text: "Most people with well-controlled conditions like diabetes or high blood pressure can donate. However, some conditions may temporarily or permanently defer you from donation.",
          icon: "üè•"
        },
        {
          subtitle: "Medications",
          text: "Many medications don't affect donation eligibility. However, some medications may require a waiting period or permanent deferral. Always inform staff about current medications.",
          icon: "üíä"
        }
      ]
    },
    benefits: {
      title: "Benefits of Blood Donation",
      content: [
        {
          subtitle: "Health Benefits",
          text: "Regular blood donation can help reduce iron levels in your body, potentially reducing the risk of heart disease. It also stimulates the production of new blood cells.",
          icon: "‚ù§Ô∏è"
        },
        {
          subtitle: "Free Health Checkup",
          text: "Before each donation, you receive a mini physical that checks your blood pressure, pulse, temperature, and hemoglobin levels. This can help identify potential health issues.",
          icon: "üîç"
        },
        {
          subtitle: "Emotional Benefits",
          text: "Knowing that your donation helps save lives provides a sense of satisfaction and purpose. It's a simple way to make a significant impact on someone's life.",
          icon: "üòä"
        }
      ]
    },
    myths: {
      title: "Common Myths Debunked",
      content: [
        {
          subtitle: "Myth: Donating blood is painful",
          text: "Fact: You may feel a slight pinch when the needle is inserted, but the donation process itself is generally painless. Most donors report minimal discomfort.",
          icon: "‚ùå"
        },
        {
          subtitle: "Myth: You can't donate if you have tattoos",
          text: "Fact: In most states, you can donate blood if your tattoo was applied by a state-regulated entity using sterile needles and ink. There's usually a 3-month waiting period.",
          icon: "üé®"
        },
        {
          subtitle: "Myth: Donating blood makes you weak",
          text: "Fact: Your body replaces the donated blood within 24-48 hours. Most people feel normal within a few hours and can resume normal activities the next day.",
          icon: "üí™"
        }
      ]
    },
    faq: {
      title: "Frequently Asked Questions",
      content: [
