import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const BloodDonationCamps = () => {
  const [selectedCity, setSelectedCity] = useState('');
  const [selectedDate, setSelectedDate] = useState('');

  // Erode-specific donation camps data
  const donationCamps = [
    {
      id: 1,
      name: "Community Blood Drive Erode 2025",
      organizer: "Erode Red Cross Society",
      city: "Erode",
      address: "Central Community Hall, Perundurai Road, Erode, TN 638001",
      date: "2025-09-30",
      time: "9:00 AM - 5:00 PM",
      expectedDonors: 150,
      registeredDonors: 89,
      description: "Join us for our annual community blood drive in Erode. Free health checkup included.",
      requirements: ["Age 18+", "Weight 110+ lbs", "Good health", "Valid ID"],
      contact: "+91 97860 12345",
      email: "erode@redcross.org"
    },
    {
      id: 2,
      name: "Corporate Blood Donation Day",
      organizer: "Erode Tech Park",
      city: "Erode",
      address: "Tech Park Auditorium, Karungalpalayam, Erode, TN 638003",
      date: "2025-10-05",
      time: "8:00 AM - 6:00 PM",
      expectedDonors: 200,
      registeredDonors: 156,
      description: "Employees and public welcome. Refreshments provided.",
      requirements: ["Age 18+", "Weight 110+ lbs", "Good health", "Valid ID"],
      contact: "+91 98765 56789",
      email: "blooddrive@erodetechpark.com"
    },
    {
      id: 3,
      name: "University Blood Donation Camp",
      organizer: "Bharathiar Medical College, Erode",
      city: "Erode",
      address: "University Medical Center, Thindal, Erode, TN 638004",
      date: "2025-10-10",
      time: "10:00 AM - 4:00 PM",
      expectedDonors: 100,
      registeredDonors: 67,
      description: "Students, faculty, and community members welcome. Educational session included.",
      requirements: ["Age 18+", "Weight 110+ lbs", "Good health", "Student ID or Valid ID"],
      contact: "+91 90920 67890",
      email: "bloodcamp@bharathiar.edu"
    },
    {
      id: 4,
      name: "Emergency Blood Collection Drive",
      organizer: "Erode City Emergency Services",
      city: "Erode",
      address: "Emergency Response Center, Surampatti, Erode, TN 638002",
      date: "2025-10-15",
      time: "24 Hours",
      expectedDonors: 300,
      registeredDonors: 234,
      description: "Emergency blood collection to replenish local blood banks in Erode.",
      requirements: ["Age 18+", "Weight 110+ lbs", "Good health", "Valid ID"],
      contact: "+91 93600 11223",
      email: "emergency@erode.gov.in"
    },
    {
      id: 5,
      name: "Faith Community Blood Drive",
      organizer: "Erode Interfaith Council",
      city: "Erode",
      address: "Community Church, Kandasamy Nagar, Erode, TN 638005",
      date: "2025-10-20",
      time: "9:00 AM - 7:00 PM",
      expectedDonors: 80,
      registeredDonors: 45,
      description: "Multi-faith community coming together to save lives in Erode.",
      requirements: ["Age 18+", "Weight 110+ lbs", "Good health", "Valid ID"],
      contact: "+91 94880 33445",
      email: "faith@erodeinterfaith.org"
    }
  ];

  const cities = [...new Set(donationCamps.map(camp => camp.city))];

  const filteredCamps = donationCamps.filter(camp => {
    const matchesCity = !selectedCity || camp.city === selectedCity;
    const matchesDate = !selectedDate || camp.date >= selectedDate;
    return matchesCity && matchesDate;
  });

  const formatDate = (dateString) => {
    const options = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString('en-US', options);
  };

  const getDaysUntil = (dateString) => {
    const today = new Date();
    const campDate = new Date(dateString);
    const diffTime = campDate - today;
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    return diffDays;
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="text-center mb-12">
          <h1 className="text-4xl font-bold text-secondary-800 mb-4">
            Blood Donation Camps
          </h1>
          <p className="text-xl text-secondary-600">
            Find and join upcoming blood donation events in Erode
          </p>
        </div>

        {/* Filters */}
        <div className="card mb-8">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <label htmlFor="city" className="form-label">
                Filter by City
              </label>
              <select
                id="city"
                value={selectedCity}
                onChange={(e) => setSelectedCity(e.target.value)}
                className="input-field"
              >
                <option value="">All Cities</option>
                {cities.map(city => (
                  <option key={city} value={city}>{city}</option>
                ))}
              </select>
            </div>
            <div>
              <label htmlFor="date" className="form-label">
                From Date
              </label>
              <input
                type="date"
                id="date"
                value={selectedDate}
                onChange={(e) => setSelectedDate(e.target.value)}
                className="input-field"
              />
            </div>
          </div>
        </div>

        {/* The rest of your component remains unchanged */}
        {/* ... */}
      </div>
    </div>
  );
};

export default BloodDonationCamps;
