import React, { useState } from 'react';
import { useAuth } from '../contexts/AuthContext';

const BloodAvailability = () => {
  const { user } = useAuth();
  const [bloodGroup, setBloodGroup] = useState('');
  const [isSearching, setIsSearching] = useState(false);
  const [searchResult, setSearchResult] = useState(null);
  const [showRequestForm, setShowRequestForm] = useState(false);
  const [requestForm, setRequestForm] = useState({
    patientName: '',
    bloodGroup: '',
    location: '',
    contact: '',
    urgency: 'normal',
    additionalInfo: ''
  });

  // Dummy blood availability data
  const bloodAvailabilityData = {
    'A+': {
      available: true,
      units: 45,
      donors: [
        { name: 'John Smith', location: 'New York', lastDonation: '2024-01-15', contact: '+1 (555) 123-4567' },
        { name: 'Sarah Johnson', location: 'Los Angeles', lastDonation: '2024-01-20', contact: '+1 (555) 987-6543' },
        { name: 'Mike Wilson', location: 'Chicago', lastDonation: '2024-01-18', contact: '+1 (555) 456-7890' }
      ]
    },
    'A-': {
      available: true,
      units: 23,
      donors: [
        { name: 'Emily Davis', location: 'Miami', lastDonation: '2024-01-22', contact: '+1 (555) 789-0123' },
        { name: 'David Brown', location: 'Seattle', lastDonation: '2024-01-19', contact: '+1 (555) 321-6540' }
      ]
    },
    'B+': {
      available: true,
      units: 38,
      donors: [
        { name: 'Lisa Anderson', location: 'Boston', lastDonation: '2024-01-21', contact: '+1 (555) 654-3210' },
        { name: 'Robert Taylor', location: 'Denver', lastDonation: '2024-01-17', contact: '+1 (555) 210-9876' }
      ]
    },
    'B-': {
      available: false,
      units: 0,
      donors: []
    },
    'AB+': {
      available: true,
      units: 15,
      donors: [
        { name: 'Jennifer Lee', location: 'San Francisco', lastDonation: '2024-01-23', contact: '+1 (555) 876-5432' }
      ]
    },
    'AB-': {
      available: false,
      units: 0,
      donors: []
    },
    'O+': {
      available: true,
      units: 67,
      donors: [
        { name: 'Michael Garcia', location: 'Phoenix', lastDonation: '2024-01-16', contact: '+1 (555) 432-1098' },
        { name: 'Amanda Martinez', location: 'Austin', lastDonation: '2024-01-24', contact: '+1 (555) 109-8765' },
        { name: 'Christopher Rodriguez', location: 'Las Vegas', lastDonation: '2024-01-14', contact: '+1 (555) 765-4321' }
      ]
    },
    'O-': {
      available: true,
      units: 12,
      donors: [
        { name: 'Jessica Thompson', location: 'Portland', lastDonation: '2024-01-25', contact: '+1 (555) 543-2109' }
      ]
    }
  };

  const bloodGroups = ['A+', 'A-', 'B+', 'B-', 'AB+', 'AB-', 'O+', 'O-'];
  const urgencyLevels = [
    { value: 'normal', label: 'Normal (Within 24 hours)', color: 'text-green-600' },
    { value: 'urgent', label: 'Urgent (Within 12 hours)', color: 'text-orange-600' },
    { value: 'emergency', label: 'Emergency (Immediate)', color: 'text-red-600' }
  ];

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!bloodGroup) return;

    setIsSearching(true);
    setSearchResult(null);
    setShowRequestForm(false);

    // Simulate API call delay
    setTimeout(() => {
      const result = bloodAvailabilityData[bloodGroup];
      setSearchResult(result);
      setIsSearching(false);
    }, 1000);
  };

  const handleRequestFormChange = (e) => {
    const { name, value } = e.target;
    setRequestForm(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleRequestSubmit = async (e) => {
    e.preventDefault();
    
    // Simulate API call
    setTimeout(() => {
      alert('Blood request submitted successfully! We will contact you shortly.');
      setShowRequestForm(false);
      setRequestForm({
        patientName: '',
        bloodGroup: '',
        location: '',
        contact: '',
        urgency: 'normal',
        additionalInfo: ''
      });
    }, 1000);
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8 pt-24">
      <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="text-center mb-12">
          <h1 className="text-4xl font-bold text-secondary-800 mb-4">
            Blood Availability Search
          </h1>
          <p className="text-xl text-secondary-600">
            Search for available blood groups and find donors in your area
          </p>
        </div>

        {/* Search Form */}
        <div className="card mb-8">
          <form onSubmit={handleSearch} className="space-y-6">
            <div>
              <label htmlFor="bloodGroup" className="form-label">
                Select Blood Group *
              </label>
              <select
                id="bloodGroup"
                value={bloodGroup}
                onChange={(e) => setBloodGroup(e.target.value)}
                className="input-field"
                required
              >
                <option value="">Choose a blood group</option>
                {bloodGroups.map(group => (
                  <option key={group} value={group}>{group}</option>
                ))}
              </select>
            </div>

            <button
              type="submit"
              disabled={isSearching || !bloodGroup}
              className="btn-primary w-full"
            >
              {isSearching ? (
                <>
                  <div className="spinner mr-2"></div>
                  Searching...
                </>
              ) : (
                'Search Availability'
              )}
            </button>
          </form>
        </div>

        {/* Search Results */}
        {searchResult && (
          <div className="card mb-8">
            <div className="text-center mb-6">
              <h2 className="text-2xl font-bold text-secondary-800 mb-2">
                Blood Group {bloodGroup} Availability
              </h2>
              <div className={`text-lg font-semibold ${
                searchResult.available ? 'text-green-600' : 'text-red-600'
              }`}>
                {searchResult.available ? (
                  <>
                    ✅ Available - {searchResult.units} units
                  </>
                ) : (
                  <>
                    ❌ Not Available
                  </>
                )}
              </div>
            </div>

            {searchResult.available ? (
              <div>
                {/* Available Units Info */}
                <div className="bg-green-50 border border-green-200 rounded-lg p-4 mb-6">
                  <div className="flex items-center justify-between">
                    <div>
                      <h3 className="text-lg font-semibold text-green-800">
                        Available Units: {searchResult.units}
                      </h3>
                      <p className="text-green-600">
                        Blood is available for immediate use
                      </p>
                    </div>
                    <div className="text-4xl">🩸</div>
                  </div>
                </div>

                {/* Donors List */}
                <div>
                  <h3 className="text-xl font-semibold text-secondary-800 mb-4">
                    Available Donors ({searchResult.donors.length})
                  </h3>
                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                    {searchResult.donors.map((donor, index) => (
                      <div key={index} className="border border-secondary-200 rounded-lg p-4 bg-white">
                        <div className="flex items-start justify-between mb-3">
                          <h4 className="font-semibold text-secondary-800">
                            {donor.name}
                          </h4>
                          <span className="text-sm text-secondary-500">
                            {formatDate(donor.lastDonation)}
                          </span>
                        </div>
                        <div className="space-y-2 text-sm text-secondary-600">
                          <div className="flex items-center">
                            <span className="text-primary-600 mr-2">📍</span>
                            {donor.location}
                          </div>
                          <div className="flex items-center">
                            <span className="text-primary-600 mr-2">📞</span>
                            <a 
                              href={`tel:${donor.contact}`}
                              className="text-primary-600 hover:text-primary-700"
                            >
                              {donor.contact}
                            </a>
                          </div>
                        </div>
                        <button className="btn-primary w-full mt-3 text-sm">
                          Contact Donor
                        </button>
                      </div>
                    ))}
                  </div>
                </div>

                {/* Action Buttons */}
                <div className="mt-6 flex flex-col sm:flex-row gap-4">
                  <button className="btn-primary flex-1">
                    Schedule Blood Collection
                  </button>
                  <button className="btn-outline flex-1">
                    Find Nearby Blood Centers
                  </button>
                </div>
              </div>
            ) : (
              <div className="text-center">
                <div className="text-6xl mb-4">🩸</div>
                <h3 className="text-xl font-semibold text-secondary-800 mb-2">
                  Blood Group {bloodGroup} is Currently Unavailable
                </h3>
                <p className="text-secondary-600 mb-6">
                  We don't have this blood group in stock right now. You can request donors to help.
                </p>
                <button
                  onClick={() => setShowRequestForm(true)}
                  className="btn-primary"
                >
                  Request Blood from Donors
                </button>
              </div>
            )}
          </div>
        )}

        {/* Blood Request Form */}
        {showRequestForm && (
          <div className="card">
            <h3 className="text-2xl font-bold text-secondary-800 mb-6">
              Request Blood from Donors
            </h3>
            <form onSubmit={handleRequestSubmit} className="space-y-6">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                  <label htmlFor="patientName" className="form-label">
                    Patient Name *
                  </label>
                  <input
                    type="text"
                    id="patientName"
                    name="patientName"
                    value={requestForm.patientName}
                    onChange={handleRequestFormChange}
                    className="input-field"
                    placeholder="Enter patient's full name"
                    required
                  />
                </div>

                <div>
                  <label htmlFor="requestBloodGroup" className="form-label">
                    Required Blood Group *
                  </label>
                  <select
                    id="requestBloodGroup"
                    name="bloodGroup"
                    value={requestForm.bloodGroup}
                    onChange={handleRequestFormChange}
                    className="input-field"
                    required
                  >
                    <option value="">Select blood group</option>
                    {bloodGroups.map(group => (
                      <option key={group} value={group}>{group}</option>
                    ))}
                  </select>
                </div>

                <div>
                  <label htmlFor="location" className="form-label">
                    Location *
                  </label>
                  <input
                    type="text"
                    id="location"
                    name="location"
                    value={requestForm.location}
                    onChange={handleRequestFormChange}
                    className="input-field"
                    placeholder="City, State"
                    required
                  />
                </div>

                <div>
                  <label htmlFor="contact" className="form-label">
                    Contact Number *
                  </label>
                  <input
                    type="tel"
                    id="contact"
                    name="contact"
                    value={requestForm.contact}
                    onChange={handleRequestFormChange}
                    className="input-field"
                    placeholder="+1 (555) 123-4567"
                    required
                  />
                </div>

                <div className="md:col-span-2">
                  <label htmlFor="urgency" className="form-label">
                    Urgency Level *
                  </label>
                  <div className="space-y-2">
                    {urgencyLevels.map(level => (
                      <label key={level.value} className="flex items-center space-x-3">
                        <input
                          type="radio"
                          name="urgency"
                          value={level.value}
                          checked={requestForm.urgency === level.value}
                          onChange={handleRequestFormChange}
                          className="text-primary-600 focus:ring-primary-500"
                        />
                        <span className={level.color}>{level.label}</span>
                      </label>
                    ))}
                  </div>
                </div>

                <div className="md:col-span-2">
                  <label htmlFor="additionalInfo" className="form-label">
                    Additional Information
                  </label>
                  <textarea
                    id="additionalInfo"
                    name="additionalInfo"
                    value={requestForm.additionalInfo}
                    onChange={handleRequestFormChange}
                    rows="4"
                    className="input-field"
                    placeholder="Any additional details about the patient or situation..."
                  />
                </div>
              </div>

              <div className="flex justify-end space-x-4 pt-6 border-t border-secondary-200">
                <button
                  type="button"
                  onClick={() => setShowRequestForm(false)}
                  className="btn-secondary"
                >
                  Cancel
                </button>
                <button type="submit" className="btn-primary">
                  Submit Request
                </button>
              </div>
            </form>
          </div>
        )}

        {/* Information Section */}
        <div className="card bg-primary-50 border-primary-200">
          <h3 className="text-xl font-semibold text-primary-800 mb-4">
            💡 How Blood Availability Works
          </h3>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6 text-sm text-primary-700">
            <div>
              <h4 className="font-semibold mb-2">1. Search</h4>
              <p>Select a blood group to check current availability in our system.</p>
            </div>
            <div>
              <h4 className="font-semibold mb-2">2. Check Results</h4>
              <p>View available units and contact information for potential donors.</p>
            </div>
            <div>
              <h4 className="font-semibold mb-2">3. Request Help</h4>
              <p>If blood is unavailable, submit a request to find donors.</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BloodAvailability;
