import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { bloodAPI } from '../services/api';

const BloodAvailability = () => {
  const [selectedBloodGroup, setSelectedBloodGroup] = useState('');
  const [availabilityResult, setAvailabilityResult] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [showRequestForm, setShowRequestForm] = useState(false);
  const [requestForm, setRequestForm] = useState({
    patientName: '',
    bloodGroup: '',
    location: '',
    contact: '',
    urgency: 'Medium',
    notes: ''
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [message, setMessage] = useState('');

  const bloodGroups = ['A+', 'A-', 'B+', 'B-', 'AB+', 'AB-', 'O+', 'O-'];
  const urgencyLevels = ['Low', 'Medium', 'High', 'Critical'];

  const handleBloodGroupChange = (e) => {
    setSelectedBloodGroup(e.target.value);
    setAvailabilityResult(null);
    setShowRequestForm(false);
  };

  const checkAvailability = async () => {
    if (!selectedBloodGroup) {
      setMessage('Please select a blood group');
      return;
    }

    setIsLoading(true);
    setMessage('');

    try {
      const response = await bloodAPI.checkAvailability(selectedBloodGroup);
      setAvailabilityResult(response.data);
      setShowRequestForm(!response.data.isAvailable);
    } catch (error) {
      setMessage('Error checking blood availability. Please try again.');
      console.error('Error:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleRequestFormChange = (e) => {
    const { name, value } = e.target;
    setRequestForm(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const submitBloodRequest = async (e) => {
    e.preventDefault();
    
    if (!requestForm.patientName || !requestForm.location || !requestForm.contact) {
      setMessage('Please fill in all required fields');
      return;
    }

    setIsSubmitting(true);
    setMessage('');

    try {
      const response = await bloodAPI.createRequest(requestForm);
      setMessage('Blood request submitted successfully! We will contact you soon.');
      setRequestForm({
        patientName: '',
        bloodGroup: '',
        location: '',
        contact: '',
        urgency: 'Medium',
        notes: ''
      });
      setShowRequestForm(false);
    } catch (error) {
      setMessage('Error submitting blood request. Please try again.');
      console.error('Error:', error);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="text-center mb-12">
          <h1 className="text-4xl font-bold text-secondary-800 mb-4">
            Blood Availability Check
          </h1>
          <p className="text-xl text-secondary-600">
            Search for available blood groups and request blood when needed
          </p>
        </div>

        {/* Blood Group Selection */}
        <div className="card mb-8">
          <h2 className="text-2xl font-semibold text-secondary-800 mb-6">
            Select Blood Group
          </h2>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-6">
            {bloodGroups.map(group => (
              <button
                key={group}
                onClick={() => setSelectedBloodGroup(group)}
                className={`p-4 rounded-lg border-2 text-center font-medium transition-colors ${
                  selectedBloodGroup === group
                    ? 'border-primary-600 bg-primary-50 text-primary-700'
                    : 'border-secondary-200 hover:border-primary-300 text-secondary-700'
                }`}
              >
                {group}
              </button>
            ))}
          </div>
          <button
            onClick={checkAvailability}
            disabled={!selectedBloodGroup || isLoading}
            className="btn-primary disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {isLoading ? (
              <>
                <div className="spinner mr-2"></div>
                Checking...
              </>
            ) : (
              'Check Availability'
            )}
          </button>
        </div>

        {/* Message Display */}
        {message && (
          <div className={`p-4 rounded-lg mb-6 ${
            message.includes('successfully') 
              ? 'bg-green-100 text-green-700 border border-green-300' 
              : 'bg-red-100 text-red-700 border border-red-300'
          }`}>
            {message}
          </div>
        )}

        {/* Availability Result */}
        {availabilityResult && (
          <div className="card mb-8">
            <h2 className="text-2xl font-semibold text-secondary-800 mb-6">
              Availability Result
            </h2>
            <div className={`p-6 rounded-lg ${
              availabilityResult.isAvailable 
                ? 'bg-green-50 border border-green-200' 
                : 'bg-red-50 border border-red-200'
            }`}>
              <div className="flex items-center mb-4">
                <div className={`w-4 h-4 rounded-full mr-3 ${
                  availabilityResult.isAvailable ? 'bg-green-500' : 'bg-red-500'
                }`}></div>
                <h3 className={`text-xl font-semibold ${
                  availabilityResult.isAvailable ? 'text-green-800' : 'text-red-800'
                }`}>
                  {availabilityResult.message}
                </h3>
              </div>
              <p className={`text-lg ${
                availabilityResult.isAvailable ? 'text-green-700' : 'text-red-700'
              }`}>
                {availabilityResult.isAvailable 
                  ? `We have ${availabilityResult.donorCount} donors available for blood group ${availabilityResult.bloodGroup}.`
                  : `Blood group ${availabilityResult.bloodGroup} is currently not available. You can request blood below.`
                }
              </p>
            </div>
          </div>
        )}

        {/* Blood Request Form */}
        {showRequestForm && (
          <div className="card">
            <h2 className="text-2xl font-semibold text-secondary-800 mb-6">
              Request Blood
            </h2>
            <form onSubmit={submitBloodRequest} className="space-y-6">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                  <label htmlFor="patientName" className="form-label">
                    Patient Name *
                  </label>
                  <input
                    type="text"
                    id="patientName"
                    name="patientName"
                    value={requestForm.patientName}
                    onChange={handleRequestFormChange}
                    className="input-field"
                    placeholder="Enter patient's full name"
                    required
                  />
                </div>

                <div>
                  <label htmlFor="bloodGroup" className="form-label">
                    Blood Group *
                  </label>
                  <select
                    id="bloodGroup"
                    name="bloodGroup"
                    value={requestForm.bloodGroup}
                    onChange={handleRequestFormChange}
                    className="input-field"
                    required
                  >
                    <option value="">Select blood group</option>
                    {bloodGroups.map(group => (
                      <option key={group} value={group}>{group}</option>
                    ))}
                  </select>
                </div>

                <div>
                  <label htmlFor="location" className="form-label">
                    Location *
                  </label>
                  <input
                    type="text"
                    id="location"
                    name="location"
                    value={requestForm.location}
                    onChange={handleRequestFormChange}
                    className="input-field"
                    placeholder="City, Hospital, or Address"
                    required
                  />
                </div>

                <div>
                  <label htmlFor="contact" className="form-label">
                    Contact Information *
                  </label>
                  <input
                    type="text"
                    id="contact"
                    name="contact"
                    value={requestForm.contact}
                    onChange={handleRequestFormChange}
                    className="input-field"
                    placeholder="Phone number or email"
                    required
                  />
                </div>

                <div>
                  <label htmlFor="urgency" className="form-label">
                    Urgency Level
                  </label>
                  <select
                    id="urgency"
                    name="urgency"
                    value={requestForm.urgency}
                    onChange={handleRequestFormChange}
                    className="input-field"
                  >
                    {urgencyLevels.map(level => (
                      <option key={level} value={level}>{level}</option>
                    ))}
                  </select>
                </div>
              </div>

              <div>
                <label htmlFor="notes" className="form-label">
                  Additional Notes
                </label>
                <textarea
                  id="notes"
                  name="notes"
                  value={requestForm.notes}
                  onChange={handleRequestFormChange}
                  className="input-field"
                  rows="3"
                  placeholder="Any additional information about the request..."
                />
              </div>

              <div className="flex justify-end space-x-4">
                <button
                  type="button"
                  onClick={() => setShowRequestForm(false)}
                  className="btn-secondary"
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  disabled={isSubmitting}
                  className="btn-primary"
                >
                  {isSubmitting ? (
                    <>
                      <div className="spinner mr-2"></div>
                      Submitting...
                    </>
                  ) : (
                    'Submit Request'
                  )}
                </button>
              </div>
            </form>
          </div>
        )}

        {/* Back to Home */}
        <div className="text-center mt-8">
          <Link to="/" className="text-primary-600 hover:text-primary-700 font-medium">
            ← Back to Home
          </Link>
        </div>
      </div>
    </div>
  );
};

export default BloodAvailability;
