import React, { useEffect, useState } from 'react';

const BloodCenterDirectory = () => {
  const [centers, setCenters] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch('http://localhost:5001/api/blood-centers')
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data)) {
          setCenters(data);
        } else if (data && Array.isArray(data.centers)) {
          setCenters(data.centers);
        } else {
          setCenters([]);
        }
        setLoading(false);
      })
      .catch((err) => {
        console.error('Error fetching centers:', err);
        setLoading(false);
      });
  }, []);

  if (loading) return <p>Loading blood centers...</p>;

  return (
    <div>
      <h2>Erode Blood Bank Directory</h2>
      {centers.length === 0 ? (
        <p>No centers found.</p>
      ) : (
        <ul>
          {centers.map((center) => (
            <li key={center._id}>
              <h3>{center.name}</h3>
              <p>{center.address}, {center.city}</p>
              <p>Phone: {center.phone}</p>
              <p>Email: {center.email}</p>
              <p>Hours: {center.hours}</p>
              {/* Directions Button */}
              <a
                href={`https://www.google.com/maps/dir/?api=1&destination=${encodeURIComponent(
                  center.address + ', ' + center.city
                )}`}
                target="_blank"
                rel="noopener noreferrer"
              >
                ðŸ©¸ Get Directions to Donate
              </a>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default BloodCenterDirectory;
