import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const Home = () => {
  const { user } = useAuth();
  const [currentSlide, setCurrentSlide] = useState(0);

  // Slideshow images from public folder
  const slides = [
    {
      id: 1,
      title: "Save Lives Through Blood Donation",
      description: "Every drop counts. Join our mission to save lives.",
      image: process.env.PUBLIC_URL + "/images/poster1.svg"
    },
    {
      id: 2,
      title: "Emergency Blood Supply",
      description: "24/7 availability for emergency situations.",
      image: process.env.PUBLIC_URL + "/images/poster2.svg"
    },
    {
      id: 3,
      title: "Community Blood Drives",
      description: "Organize and participate in community blood donation events.",
      image: process.env.PUBLIC_URL + "/images/poster3.svg"
    }
  ];

  // Auto-advance slideshow
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 5000);

    return () => clearInterval(timer);
  }, [slides.length]);

  const services = [
    {
      id: 1,
      title: "Blood Availability Search",
      description: "Quickly find available blood groups in your area",
      icon: "🩸",
      link: "/availability"
    },
    {
      id: 2,
      title: "Blood Center Directory",
      description: "Locate nearby blood banks and donation centers",
      icon: "🏥",
      link: "#"
    },
    {
      id: 3,
      title: "Blood Donation Camps",
      description: "Find and join upcoming donation events",
      icon: "🎪",
      link: "#"
    },
    {
      id: 4,
      title: "Donor Login",
      description: "Access your donor profile and history",
      icon: "👤",
      link: "/login"
    },
    {
      id: 5,
      title: "Register Voluntary Blood Camp",
      description: "Organize your own blood donation event",
      icon: "📋",
      link: "#"
    },
    {
      id: 6,
      title: "Learn About Donation",
      description: "Educational resources about blood donation",
      icon: "📚",
      link: "#"
    }
  ];

  return (
    <div className="min-h-screen">
      {/* Hero Section with Slideshow */}
      <section className="relative h-96 md:h-[500px] overflow-hidden">
        {slides.map((slide, index) => (
          <div
            key={slide.id}
            className={`absolute inset-0 transition-opacity duration-1000 ${
              index === currentSlide ? 'opacity-100' : 'opacity-0'
            }`}
          >
            <div className="absolute inset-0 bg-black bg-opacity-40 z-10"></div>
            <img
              src={slide.image}
              alt={slide.title}
              className="w-full h-full object-cover"
            />
            <div className="absolute inset-0 z-20 flex items-center justify-center">
              <div className="text-center text-white px-4">
                <h1 className="text-4xl md:text-6xl font-bold mb-4 animate-fade-in">
                  {slide.title}
                </h1>
                <p className="text-xl md:text-2xl mb-8 animate-slide-up">
                  {slide.description}
                </p>
                {!isAuthenticated && (
                  <div className="space-x-4">
                    <Link to="/register" className="btn-primary text-lg px-8 py-3">
                      Get Started
                    </Link>
                    <Link to="/login" className="btn-outline text-lg px-8 py-3 border-white text-white hover:bg-white hover:text-primary-600">
                      Sign In
                    </Link>
                  </div>
                )}
              </div>
            </div>
          </div>
        ))}
        
        {/* Slideshow Indicators */}
        <div className="absolute bottom-8 left-1/2 transform -translate-x-1/2 z-30 flex space-x-2">
          {slides.map((_, index) => (
            <button
              key={index}
              onClick={() => setCurrentSlide(index)}
              className={`w-3 h-3 rounded-full transition-colors ${
                index === currentSlide ? 'bg-white' : 'bg-white bg-opacity-50'
              }`}
            />
          ))}
        </div>
      </section>

      {/* Our Services Section */}
      <section className="py-16 bg-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-12">
            <h2 className="text-3xl font-bold text-secondary-800 mb-4">
              Our Services
            </h2>
            <p className="text-xl text-secondary-600">
              Everything you need for blood donation and management
            </p>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {/* Blood Availability Search */}
            <div className="card text-center hover:shadow-xl transition-shadow duration-200">
              <div className="text-4xl mb-4">🔍</div>
              <h3 className="text-xl font-semibold text-secondary-800 mb-3">
                Blood Availability Search
              </h3>
              <p className="text-secondary-600 mb-4">
                Search for available blood by blood group and request donors if needed
              </p>
              <Link to="/availability" className="btn-primary">
                Search Blood
              </Link>
            </div>

            {/* Blood Center Directory */}
            <div className="card text-center hover:shadow-xl transition-shadow duration-200">
              <div className="text-4xl mb-4">🏥</div>
              <h3 className="text-xl font-semibold text-secondary-800 mb-3">
                Blood Center Directory
              </h3>
              <p className="text-secondary-600 mb-4">
                Find blood banks and donation centers near you with detailed information
              </p>
              <Link to="/directory" className="btn-primary">
                Find Centers
              </Link>
            </div>

            {/* Blood Donation Camps */}
            <div className="card text-center hover:shadow-xl transition-shadow duration-200">
              <div className="text-4xl mb-4">🎪</div>
              <h3 className="text-xl font-semibold text-secondary-800 mb-3">
                Blood Donation Camps
              </h3>
              <p className="text-secondary-600 mb-4">
                Discover upcoming blood donation events and camps in your area
              </p>
              <Link to="/camps" className="btn-primary">
                View Camps
              </Link>
            </div>

            {/* Donor Login */}
            <div className="card text-center hover:shadow-xl transition-shadow duration-200">
              <div className="text-4xl mb-4">🔐</div>
              <h3 className="text-xl font-semibold text-secondary-800 mb-3">
                Donor Login
              </h3>
              <p className="text-secondary-600 mb-4">
                Access your donor account to manage donations and requests
              </p>
              {!user ? (
                <Link to="/login" className="btn-primary">
                  Login
                </Link>
              ) : (
                <div className="text-primary-600 font-medium">
                  Welcome back, {user.name}!
                </div>
              )}
            </div>

            {/* Register Voluntary Blood Camp */}
            <div className="card text-center hover:shadow-xl transition-shadow duration-200">
              <div className="text-4xl mb-4">📝</div>
              <h3 className="text-xl font-semibold text-secondary-800 mb-3">
                Register Voluntary Blood Camp
              </h3>
              <p className="text-secondary-600 mb-4">
                Organize your own blood donation event and help save lives
              </p>
              <Link to="/register-camp" className="btn-primary">
                Register Camp
              </Link>
            </div>

            {/* Learn About Donation */}
            <div className="card text-center hover:shadow-xl transition-shadow duration-200">
              <div className="text-4xl mb-4">📚</div>
              <h3 className="text-xl font-semibold text-secondary-800 mb-3">
                Learn About Donation
              </h3>
              <p className="text-secondary-600 mb-4">
                Get educated about blood donation process, benefits, and myths
              </p>
              <Link to="/learn" className="btn-primary">
                Learn More
              </Link>
            </div>
          </div>
        </div>
      </section>

      {/* Call to Action Section */}
      <section className="bg-primary-600 py-16 px-4">
        <div className="max-w-4xl mx-auto text-center text-white">
          <h2 className="text-3xl md:text-4xl font-bold mb-6">
            Ready to Make a Difference?
          </h2>
          <p className="text-xl mb-8 opacity-90">
            Join thousands of donors who are saving lives every day. Your donation can make a world of difference.
          </p>
          {!isAuthenticated ? (
            <div className="space-x-4">
              <Link to="/register" className="bg-white text-primary-600 hover:bg-gray-100 font-medium py-3 px-8 rounded-lg transition-colors duration-200">
                Become a Donor
              </Link>
              <Link to="/login" className="border-2 border-white text-white hover:bg-white hover:text-primary-600 font-medium py-3 px-8 rounded-lg transition-colors duration-200">
                Sign In
              </Link>
            </div>
          ) : (
            <Link to="/availability" className="bg-white text-primary-600 hover:bg-gray-100 font-medium py-3 px-8 rounded-lg transition-colors duration-200">
              Check Blood Availability
            </Link>
          )}
        </div>
      </section>
    </div>
  );
};

export default Home;
