import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const Home = () => {
  const { user } = useAuth();

  return (
    <div className="min-h-screen">
      {/* HERO - Single Slide */}
      <section className="relative h-96 md:h-[500px] overflow-hidden isolate">
        {/* Background image */}
        <img
          src={process.env.PUBLIC_URL + "/images/poster1.svg"}
          alt="Blood Donation"
          className="w-full h-full object-cover"
        />

        {/* Dark overlay */}
        <div className="absolute inset-0 bg-black/70 z-10" />

        {/* Foreground text */}
        <div className="absolute inset-0 z-20 flex items-center justify-center px-4">
          <div className="text-center text-white">
            <h1 className="text-4xl md:text-6xl font-extrabold mb-4">
              Save Lives Through Blood Donation
            </h1>
            <p className="text-lg md:text-2xl mb-6 opacity-95">
              Every drop counts. Join our mission to save lives.
            </p>
            {!user && (
              <div className="flex items-center justify-center gap-4">
                <Link to="/register" className="btn-primary text-lg px-8 py-3">
                  Get Started
                </Link>
                <Link
                  to="/login"
                  className="btn-outline text-lg px-8 py-3 border-white text-white hover:bg-white hover:text-primary-600"
                >
                  Sign In
                </Link>
              </div>
            )}
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;
